$CHIRP_APP_SECRET = "8E2bFf3C82a5ACA00DFE4B3d3Ed891BbEb99b99624b144dBFa"
$CHIRP_APP_CONFIG = ""

$AssemblyPath = Join-Path $PSScriptRoot 'ChirpConnectNet-x64.dll'
[System.Reflection.Assembly]::LoadFile($AssemblyPath)
$connect = [ChirpConnectNet.ChirpConnect]::new($CHIRP_APP_KEY, $CHIRP_APP_SECRET, $CHIRP_APP_CONFIG)
